mapper.update:
update member set update_time = now()

	, address = #{address}


	, birthday = #{birthday}


	, member_point = #{member_point}


	, experience = #{experience}


	, id_card_no = #{id_card_no}


	, member_card_no = #{member_card_no}


	, member_level_id = #{member_level_id}

where uid = #{uid} and mid = #{mid} and status = 0
