mapper.select:
select uid, mid, is_member, identify, name, nick, gender, update_time,
status from user where uid = #{uid} and status != -1

limit #{offset}, #{limit}

mapper.update:
		update user set update_time = now()
            
                , identify = #{identify}
            
            
                , name = #{name}
            
            
                , nick = #{nick}
            
            
                , gender = #{gender}
            
            
                , status = #{status}
            
		where uid = #{uid} and mid = #{mid}
